enum FavColor { red, blue }class Human {  int tall;  String gender;  Human({required this.tall, required this.gender});  void whatIsGender() {    print("I'm a ${gender}");  }}class User extends Human {  String name;  int age;  int tall;  String gender;  FavColor favColor;  String location;  User(      {required this.name,      required this.age,      required this.favColor,      required this.location,      required this.gender,      required this.tall}) : super(tall: tall, gender: gender);  void sayHello() {    print(        "Hi, My name is $name. I'm $age old and I live in $location. Also My favorite color is $favColor. So I buy clothes that has this $favColor.  ");  }  @override void whatIsGender() {    super.whatIsGender();    if(this.tall >= 180){      print("I'm tall");    }else{      print("I'm short");    }  }}void main() {  var byungmin = User(      name: "byungmin",      age: 30,      favColor: FavColor.blue,      location: "Gwangmyeong",      gender: "man",      tall: 178  )    ..name = "test"    ..age = 31    ..favColor = FavColor.red    ..sayHello();}